    <div class="d-flex justify-content-center ">
        <div class="container ">
        <div class="card text-white bg-secondary mb-2" style="max-width: 0rem;" center >
            <div class="card-body">
            <h1 class="card-title">Bienvenido a Crear Usuario</h1>
            <form>
                <div class="form-group">
                <label class="form-label mt-2">Ingreso de Datos</label>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control"[(ngModel)]="user.nombre" id="nombre" formControlName="nombre" placeholder="">
                    <label for="nombre">Nombre</label>
                </div>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control" id="apellido"  [(ngModel)]="user.apellido" formControlName="apellido"placeholder="">
                    <label for="apellido">Apellido</label>
                </div>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control" id="telefono" [(ngModel)]="user.telefono" formControlName="telefono" placeholder="">
                    <label for="telefono">Telefono</label>
                </div>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control" id="correo" [(ngModel)]="user.correo" formControlName="correo" placeholder="">
                    <label for="correo">Correo</label>
                </div>
                </div>
                <div class="d-flex justify-content-center">
                <div class="row">
                    <div class="col">
                    <button type="button" (click)="crear()" class="btn btn-primary"><strong> Crear </strong></button>
                    </div>
                </div>
                </div>
            </form>
            </div>
        </div>
    </div>
